import { useState } from "react";
import { useLocation } from 'react-router-dom';
import './Final.css';

const Final = () => {
    const [state, setState] = useState(false);
  const [number, setNumber] = useState(0);
  const location = useLocation();
  const inputDataWrapper = location.state;

  if (!inputDataWrapper || !inputDataWrapper.inputData || !inputDataWrapper.inputData.length || !inputDataWrapper.inputData[0]) {
    // Handle the case when inputData is undefined, an empty array, or the first element is undefined
    console.error("Invalid inputData:", inputDataWrapper);
    return null;
  }

  function handleNext(){
    if(number<inputData.length)
    setNumber(prevNumber => prevNumber+1);

  }
  function handlePrev(){
    if(number>0)setNumber(prevNumber => prevNumber-1);
    else setNumber(0);
  }
  const inputData = inputDataWrapper.inputData;

  const style = state ? inputData[number].question : inputData[number].answer;

  function reverseState() {
    setState((prevState) => !prevState);
  }

  return (
    <div className='quizCard' onClick={reverseState}>
      <p>{style}</p>
      <button onClick = {handleNext}>Next</button>
      <button onClick = {handlePrev}>Previous</button>
    </div>
  );
};

export default Final;
