import { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import './Final.css';
import { loadData } from './storage'; // Adjust the path accordingly

const Final = () => {
  const location = useLocation();
  const inputData = location.state;

  // If inputData is not available in location state, try loading it from local storage
  if (!inputData || !inputData.length) {
    const savedData = loadData('questionAnswerData');
    if (savedData) {
      inputData = savedData;
    } else {
      // Handle the case when inputData is still not available
      console.error('Invalid inputData:', inputData);
      return null;
    }
  }

  const [state, setState] = useState(false);
  const style = state ? inputData[0].question : inputData[0].answer;

  useEffect(() => {
    // You can use the data from local storage here, if needed
    console.log('Data loaded in Final component:', inputData);
  }, [inputData]);

  function reverseState() {
    setState((prevState) => !prevState);
  }

  return (
    <div className='quizCard' onClick={reverseState}>
      <p>{style}</p>
    </div>
  );
};

export default Final;
