import './MainComponent.css';
import React from 'react';
import { useState } from 'react';
import LineComp from './LineComp';
import { useNavigate } from 'react-router-dom';
import Final from './Final';
const MainComponent = () => {
  let navigate = useNavigate();
  function switchScreen() {
    navigate('/final');
  }
  const [lines, setLines] = useState([]);
  function addLine(question, answer){
    setLines(prevLines => {
      return [...prevLines,{question,answer}];
    })
  }
  const elements = lines.map((line,index) => <LineComp key = {index} onAddLine= {(question, answer) => addLine(question, answer)}/>)

  return (
    <div>
      <div>
        {elements}
      </div>
      <button onClick={() => addLine('', '')}>Add</button>
      <button onClick={switchScreen}>Finish</button>

    </div>
  );
};

export default MainComponent;
